rm build/resp.xml; xml2wbxml -a -o build/request.wbxml build/$command$.xml; curl --url 'https://$host$/Microsoft-Server-ActiveSync?Cmd=$command$&User=$user$&DeviceId=$device_id$&DeviceType=Android' --user $user$:$password$ -H "MS-ASProtocolVersion: 14.1" -H "User-Agent: Android/Android" -H "Accept-Language: en-US" -H "Content-Type: application/vnd.ms-sync.wbxml" -H "Host: $host$" --data-binary @build/request.wbxml  -H "X-MS-PolicyKey: $policy_key$" -k > build/resp.wbxml; wbxml2xml -l ACTIVESYNC -o build/resp.xml build/resp.wbxml;
